$(function(){if($("#search_query").length>0){$("#search_query").autocomplete({source:function(b,a){$.ajax({type:"POST",url:"/search",dataType:"json",data:{format:"json",term:b.term,area:$("input[type='radio'][name='area']:checked").val()},success:function(c){a(c)}})},minLength:3}).data("ui-autocomplete")._renderItem=function(a,b){return $("<li></li>").append("<a><strong>"+b.value+"</strong> - "+b.desc+"</a>").appendTo(a)}}});