(function(){Dropzone.options.avatarUpload={maxFilesize:3,autoProcessQueue:false,maxFiles:1,thumbnailWidth:512,thumbnailHeight:512,acceptedFiles:".jpeg,.jpg,.png,.gif,.JPEG,.JPG,.PNG,.GIF",uploadMultiple:false,accept:function(b,a){console.log("Uploaded");return a()},init:function(){this.on("maxfilesexceeded",function(a){return console.log("Reached limit"+a.name)});this.on("addedfile",function(a){console.log(""+a.name+" added");if(this.files.length>1){return this.removeFile(this.files[0])}});this.on("success",function(a){console.log("successfully uploaded");return window.location.reload()});return this.on("thumbnail",function(a,b){$("input#avatar-original-width").val(a.width);$("input#avatar-original-height").val(a.height);return this.processQueue()})}}}).call(this);