Dropzone.options.avatarUpload={maxFilesize:3,autoProcessQueue:false,maxFiles:1,thumbnailWidth:512,thumbnailHeight:512,acceptedFiles:".jpeg,.jpg,.png,.gif,.JPEG,.JPG,.PNG,.GIF",uploadMultiple:false,accept:function(c,d){console.log("Uploaded");return d()},init:function(){this.on("maxfilesexceeded",function(b){return console.log("Reached limit"+b.name)});this.on("addedfile",function(b){console.log(""+b.name+" added");if(this.files.length>1){return this.removeFile(this.files[0])}});this.on("success",function(b){console.log("successfully uploaded");return window.location.reload()});return this.on("thumbnail",function(d,c){$("input#avatar-original-width").val(d.width);$("input#avatar-original-height").val(d.height);return this.processQueue()})}};